INSERT INTO Paragraphs (ParagraphID, TopicID, Content) VALUES (2, 2, 'Notation for Complex Numbers Several different mathematical notations can be used to represent complex numbers. The two basic types are polar form and rectangular form. Converting between them quickly and easily is an important skill.');
