INSERT INTO Answers (AnswerID, QuestionID, AnswerText, StartPosition, EndPosition) VALUES (2260, 'enwt1brbh0i03znuj9u51r21c', 'infinite number', 200, 215);
INSERT INTO Questions (QuestionID, ParagraphID, QuestionText, IsImpossible) VALUES ('b7avmyihbwns3nzihp7fegye7', 105, ' What will we use to explain the sampling process?', False);
INSERT INTO Answers (AnswerID, QuestionID, AnswerText, StartPosition, EndPosition) VALUES (2261, 'b7avmyihbwns3nzihp7fegye7', 'frequency spectrum', 67, 85);
